import styles from './StatusAdd.module.css';

const StatusAdd = (userName: string): JSX.Element => {

    return (
        <div className={styles.container}>
            <form>
                <textarea placeholder={`What's on your mind, ${userName}?`}/>
                <button>Post</button>
            </form>
        </div>
    )
}

export default StatusAdd;
