import { createContext } from "react";


export const UserContext = createContext(null);

export const userReducer = (state: any, action: any) => {
    switch(action.type){
        case 'LOGIN_USER': {
            return {
                user: action.payload
            }
        }
        case 'LOGOUT_USER': {
            return {
                user: null
            }
        }
    }
}
