import { MouseEvent, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useUserContext } from "../hooks/useUserContext";
import styles from './Profile.module.css';
import { CiLocationOn } from 'react-icons/ci';
import { BsCardText } from 'react-icons/bs';
import { User } from "../models/User";
import Dialog from "./Dialog";

const Profile = (): JSX.Element => {
    const { state } = useUserContext();
    const user = state.user;
    const [isOwnProfile, setIsOwnProfile] = useState<boolean>(false);
    const [showBioDialog, setShowBioDialog] = useState<boolean>(false);
    const [showLocationDialog, setShowLocationDialog] = useState<boolean>(false);
    const usernameParam = useParams();
    let currUser: User | null = null;

    const handleLocationDialogOpen = (ev: MouseEvent<HTMLDivElement, globalThis.MouseEvent>) => {
        ev.preventDefault();
        setShowLocationDialog(true);
    }

    const handleBioDialogOpen = (ev: MouseEvent<HTMLDivElement, globalThis.MouseEvent>) => {
        ev.preventDefault();
        setShowBioDialog(true);
    }

    const handleLocationUpdate = (value?: string) => {
        setShowLocationDialog(false);
        updateField('location', value);
    }

    const handleBioUpdate = (value?: string) => {
        setShowBioDialog(false);
        updateField('bio', value);
    }

    const updateField = async (fieldName: string, value?: String) => {
        const body = {
            username: user?.username,
            fieldName: fieldName,
            data: value
        }
        const response = await fetch('http://localhost:4000/users/updateField', {
            method: 'POST',
            body: JSON.stringify(body),
            headers: {
                'Content-Type': 'application/json'
            }
        });


    }

    useEffect(() => {
        const username = usernameParam.username;
        let userStr = localStorage.getItem('user');
        if (userStr){
            currUser = JSON.parse(userStr);
        }
        if (username === user?.username || username === currUser?.username)
            setIsOwnProfile(true);
    }, [])

    return (
        <>
            {isOwnProfile && 
            <div className={styles.container}>
                <div className={styles.profileInfo}>
                    <img src={`http://localhost:4000/uploads/${user?.photo}`} className={styles.img}></img>
                    <div className={styles.name}>{user?.name} {user?.surname}</div>
                    <div className={styles.username}>@{user?.username}</div>
                    <div className={styles.location}>
                        <CiLocationOn style={{fontSize: '1.5em'}}/> 
                        <span className={styles.locationText}>{user?.location || <div className={styles.addLocation} onClick={(ev) => handleLocationDialogOpen(ev)}>Add location.</div>}</span>
                    </div>
                    <div className={styles.bio}>
                        <BsCardText style={{ fontSize: '1.3em' }} />
                        <span className={styles.bioText}>{user?.bio || <div className={styles.addBio} onClick={(ev) => handleBioDialogOpen(ev)}>Add bio.</div>}</span>
                    </div>
                </div>


                {showBioDialog && (
                    <Dialog
                        title="Edit Bio"
                        onClose={() => setShowBioDialog(false)}
                        onSave={handleBioUpdate}
                        defaultValue={user?.bio}
                        isOpen={showBioDialog}
                    />
                )}

                {showLocationDialog && (
                    <Dialog
                        title="Edit Location"
                        onClose={() => setShowLocationDialog(false)}
                        onSave={handleLocationUpdate}
                        defaultValue={user?.location}
                        isOpen={showLocationDialog}
                    />
                )}
            </div>
            }
            
            
        </>
    )
}

export default Profile;
