import { MouseEvent, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useUserContext } from "../hooks/useUserContext";
import styles from './Profile.module.css';
import { CiLocationOn } from 'react-icons/ci';
import { BsCardText } from 'react-icons/bs';
import { User } from "../models/User";

const Profile = (): JSX.Element => {
    const { state } = useUserContext();
    const user = state.user;
    const [isOwnProfile, setIsOwnProfile] = useState<boolean>(false);
    const usernameParam = useParams();
    let currUser: User | null = null;

    const handleChangeLocation = (ev: MouseEvent<HTMLDivElement, globalThis.MouseEvent>) => {
        ev.preventDefault();

    }

    const handleChangeBio = (ev: MouseEvent<HTMLDivElement, globalThis.MouseEvent>) => {
        ev.preventDefault();
        
    }

    useEffect(() => {
        const username = usernameParam.username;
        let userStr = localStorage.getItem('user');
        if (userStr){
            currUser = JSON.parse(userStr);
        }
        if (username === user?.username || username === currUser?.username)
            setIsOwnProfile(true);
    }, [])

    return (
        <>
            {isOwnProfile && 
            <div className={styles.container}>
                <div className={styles.profileInfo}>
                    <img src={`http://localhost:4000/uploads/${user?.photo}`} className={styles.img}></img>
                    <div className={styles.name}>{user?.name} {user?.surname}</div>
                    <div className={styles.username}>@{user?.username}</div>
                    <div className={styles.location}>
                        <CiLocationOn style={{fontSize: '1.5em'}}/> 
                        <span className={styles.locationText}>{user?.location || <div className={styles.addLocation} onClick={(ev) => handleChangeLocation(ev)}>Add location.</div>}</span>
                    </div>
                    <div className={styles.bio}>
                        <BsCardText style={{ fontSize: '1.3em' }} />
                        <span className={styles.bioText}>{user?.bio || <div className={styles.addBio} onClick={(ev) => handleChangeBio(ev)}>Add bio.</div>}</span>
                    </div>
                </div>
            </div>
            }
            
            
        </>
    )
}

export default Profile;
