import { useEffect } from "react";
import { useUserContext } from "../hooks/useUserContext";
import StatusAdd from "../status_add/StatusAdd";
import styles from './Feed.module.css';
import useFetch from "../hooks/useFetch";
import StatusList from "../status_list/StatusList";
import { useStatusContext } from "../hooks/useStatusContext";
import { SET_STATUSES } from "../contexts/StatusContext";

const Feed = (): JSX.Element => {
    const { state } = useUserContext();
    const user = state.user;
    //const { data } = useFetch('http://localhost:4000/statuses');
    const { dispatch, state: statusState } = useStatusContext();

    useEffect(() => {
        const fetchStatuses = async () => {
            const response = await fetch('http://localhost:4000/statuses')
            const json = await response.json()
            
            console.log(json)
            dispatch({type: SET_STATUSES, payload: json})
          }
      
          fetchStatuses()
    }, [])

    return (
        <>
            {   user && (
                <div className={styles.container}>
                    {/*<h2>Welcome to The Social App Feed, {user && user.name}!</h2>*/}
                    <StatusAdd userName = {user.name} />
                    <StatusList></StatusList>
                </div>
                )
            }
        </>
    );
}

export default Feed;
