import { createContext, useReducer } from "react";
import { Status } from "../models/Status";

export const ADD_STATUS = typeof 'ADD_STATUS';

type StatusState = {
    statuses: Status[];
}

type StatusAction = {
    type: typeof ADD_STATUS;
    payload?: Status[];
}

export const StatusContext = createContext<{state: StatusState; dispatch: React.Dispatch<StatusAction>} | undefined>(undefined);

export const statusReducer = (state: any, action: any) => {
    switch(action.type){
        case ADD_STATUS:{
            return {
                statuses: [action.payload, ...state.statuses]
            }
        }
        default:
            return state;
    }
}

export const StatusContextProvider = ({ children }: { children: React.ReactNode }) => {
    const [state, dispatch] = useReducer(statusReducer, {statuses: []});

    return (
        <StatusContext.Provider value={{...state, dispatch}}>
            { children }
        </StatusContext.Provider>
    )
}
